  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all mails as @mails" do
      mail = Mail.create! valid_attributes
      get :index
      assigns(:mails).should eq([mail])
    end
  end

  describe "GET show" do
    it "assigns the requested mail as @mail" do
      mail = Mail.create! valid_attributes
      get :show, :id => mail.id.to_s
      assigns(:mail).should eq(mail)
    end
  end

  describe "GET new" do
    it "assigns a new mail as @mail" do
      get :new
      assigns(:mail).should be_a_new(Mail)
    end
  end

  describe "GET edit" do
    it "assigns the requested mail as @mail" do
      mail = Mail.create! valid_attributes
      get :edit, :id => mail.id.to_s
      assigns(:mail).should eq(mail)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Mail" do
        expect {
          post :create, :mail => valid_attributes
        }.to change(Mail, :count).by(1)
      end

      it "assigns a newly created mail as @mail" do
        post :create, :mail => valid_attributes
        assigns(:mail).should be_a(Mail)
        assigns(:mail).should be_persisted
