        assigns(:mail).should be_a_new(Mail)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Mail.any_instance.stub(:save).and_return(false)
        post :create, :mail => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested mail" do
        mail = Mail.create! valid_attributes
        # Assuming there are no other mails in the database, this
        # specifies that the Mail created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Mail.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => mail.id, :mail => {'these' => 'params'}
      end

      it "assigns the requested mail as @mail" do
        mail = Mail.create! valid_attributes
